"use strict";const baseUrl="http://localhost:3000/notes",$noteList=document.querySelector(".js-note-list"),getNotesData=()=>new Promise((e,t)=>{fetch(baseUrl,{method:"GET"}).then(t=>{e(t.json())}).catch(e=>{t(e)})}),printCards=function(e){const t=document.querySelector("#note-card-template").innerHTML;e.forEach(e=>{const n=t.replace("{heading}",e.title).replace("{image}",e.thumbnail).replace("{date}",e.time).replace("{creator}",e.creator).replace("{text}",e.content);$noteList.innerHTML+=n})},init=()=>{getNotesData().then(e=>{printCards(e)}).catch(e=>{console.log(e)})};document.addEventListener("DOMContentLoaded",init,!1);